function BlogController(n,t){var i=this;i.postSearch="";i.categoriaFilter="";i.postagens=null;i.postagem=null;i.categorias=null;i.init=function(){if(window.location.pathname.includes("Post")){let n=window.location.pathname.split("/"),t=parseInt(n[n.length-1]);i.getPost(t)}i.getBlog()};i.getBlog=function(){t({method:"GET",url:window.location.protocol+"//"+window.location.host+"/Service/GetBlogContent"}).then(function(n){n!=null&&(i.postagens=n.data.postagens,i.categorias=n.data.categorias)})};i.handleDates=function(n){let t="";if(n!=null){let r=n.data.split("T")[0],i=r.split("-");t=i[2]+"/"+i[1]+"/"+i[0]}return t};i.goToBlogContentPage=function(n){window.location=window.location.protocol+"//"+window.location.host+"/Blog/Post/"+n};i.getPost=function(n){t({method:"POST",url:window.location.protocol+"//"+window.location.host+"/Service/GetPostById?postId="+n}).then(function(n){n!=null&&(i.postagem=n.data)})};i.filterByCategoria=function(n){i.categoriaFilter===""?i.categoriaFilter=n.nome:i.categoriaFilter==n.nome&&(i.categoriaFilter="")}}angular.module("logosApp").controller("BlogController",["$scope","$http",BlogController]);