function ProfessoresPanelController(n,t){var i=this;i.professores=null;i.professor=null;i.init=function(){i.getProfessores()};i.getProfessores=function(){t({method:"GET",url:window.location.protocol+"//"+window.location.host+"/Service/GetProfessores"}).then(function(n){n!=null&&(i.professores=n.data)})};i.showProfDetails=function(n){i.professor=n;$("#professor-details").show();i.scrollToProfDetails()};i.closeProfDetails=function(){$("#professor-details").hide();i.professor=null};i.scrollToProfDetails=function(){$("html, body").animate({scrollTop:$("#professor-details").offset().top-100},1e3)};i.goToProfessoresPage=function(){window.location=window.location.protocol+"//"+window.location.host+"/Professores"}}angular.module("logosApp").controller("ProfessoresPanelController",["$scope","$http",ProfessoresPanelController]);